function checkOptions() {

    // dark
    if (localStorage.getItem('ynexdarktheme')) {
        let darkThemeEl = document.querySelector('#switcher-dark-theme');
        if (darkThemeEl) darkThemeEl.checked = true;
    }

    // horizontal
    if (localStorage.getItem('ynexlayout') === "horizontal") {
        let horizontalEl = document.querySelector('#switcher-horizontal');
        let menuClickEl = document.querySelector('#switcher-menu-click');
        if (horizontalEl) horizontalEl.checked = true;
        if (menuClickEl) menuClickEl.checked = true;
    }
    else {
        let verticalEl = document.querySelector('#switcher-vertical');
        if (verticalEl) verticalEl.checked = true;
    }

    //RTL
    if (localStorage.getItem('ynexrtl')) {
        let rtlEl = document.querySelector('#switcher-rtl');
        if (rtlEl) rtlEl.checked = true;
    }
    else {
        let ltrEl = document.querySelector('#switcher-ltr');
        if (ltrEl) ltrEl.checked = true;
    }

    // light header
    if (localStorage.getItem('ynexHeader') === "light") {
        let headerLightEl = document.querySelector('#switcher-header-light');
        if (headerLightEl) headerLightEl.checked = true;
    }

    // color header
    if (localStorage.getItem('ynexHeader') === "color") {
        let headerPrimaryEl = document.querySelector('#switcher-header-primary');
        if (headerPrimaryEl) headerPrimaryEl.checked = true;
    }

    // gradient header
    if (localStorage.getItem('ynexHeader') === "gradient") {
        let headerGradientEl = document.querySelector('#switcher-header-gradient');
        if (headerGradientEl) headerGradientEl.checked = true;
    }

    // dark header
    if (localStorage.getItem('ynexHeader') === "dark") {
        let headerDarkEl = document.querySelector('#switcher-header-dark');
        if (headerDarkEl) headerDarkEl.checked = true;
    }
    // transparent header
    if (localStorage.getItem('ynexHeader') === "transparent") {
        let headerTransparentEl = document.querySelector('#switcher-header-transparent');
        if (headerTransparentEl) headerTransparentEl.checked = true;
    }

    // light menu
    if (localStorage.getItem('ynexMenu') === 'light') {
        let menuLightEl = document.querySelector('#switcher-menu-light');
        if (menuLightEl) menuLightEl.checked = true;
    }

    // color menu
    if (localStorage.getItem('ynexMenu') === 'color') {
        let menuPrimaryEl = document.querySelector('#switcher-menu-primary');
        if (menuPrimaryEl) menuPrimaryEl.checked = true;
    }

    // gradient menu
    if (localStorage.getItem('ynexMenu') === 'gradient') {
        let menuGradientEl = document.querySelector('#switcher-menu-gradient');
        if (menuGradientEl) menuGradientEl.checked = true;
    }

    // dark menu
    if (localStorage.getItem('ynexMenu') === 'dark') {
        let menuDarkEl = document.querySelector('#switcher-menu-dark');
        if (menuDarkEl) menuDarkEl.checked = true;
    }
    // transparent menu
    if (localStorage.getItem('ynexMenu') === 'transparent') {
        let menuTransparentEl = document.querySelector('#switcher-menu-transparent');
        if (menuTransparentEl) menuTransparentEl.checked = true;
    }

    //boxed
    if (localStorage.getItem('ynexboxed')) {
        let boxedEl = document.querySelector('#switcher-boxed');
        if (boxedEl) boxedEl.checked = true;
    }

    //scrollable
    if (localStorage.getItem('ynexheaderscrollable')) {
        let headerScrollEl = document.querySelector('#switcher-header-scroll');
        if (headerScrollEl) headerScrollEl.checked = true;
    }
    if (localStorage.getItem('ynexmenuscrollable')) {
        let menuScrollEl = document.querySelector('#switcher-menu-scroll');
        if (menuScrollEl) menuScrollEl.checked = true;
    }

    //fixed
    if (localStorage.getItem('ynexheaderfixed')) {
        let headerFixedEl = document.querySelector('#switcher-header-fixed');
        if (headerFixedEl) headerFixedEl.checked = true;
    }
    if (localStorage.getItem('ynexmenufixed')) {
        let menuFixedEl = document.querySelector('#switcher-menu-fixed');
        if (menuFixedEl) menuFixedEl.checked = true;
    }

    //classic
    if (localStorage.getItem('ynexclassic')) {
        let classicEl = document.querySelector('#switcher-classic');
        if (classicEl) classicEl.checked = true;
    }

    //modern
    if (localStorage.getItem('ynexmodern')) {
        let modernEl = document.querySelector('#switcher-modern');
        if (modernEl) modernEl.checked = true;
    }

    // sidemenu layout style
    if (localStorage.ynexverticalstyles) {
        let verticalStyles = localStorage.getItem('ynexverticalstyles');
        switch (verticalStyles) {
            case 'default':
                let defaultMenuEl = document.querySelector('#switcher-default-menu');
                if (defaultMenuEl) defaultMenuEl.checked = true;
                break;
            case 'closed':
                let closedMenuEl = document.querySelector('#switcher-closed-menu');
                if (closedMenuEl) closedMenuEl.checked = true;
                break;
            case 'icontext':
                let icontextMenuEl = document.querySelector('#switcher-icontext-menu');
                if (icontextMenuEl) icontextMenuEl.checked = true;
                break;
            case 'overlay':
                let overlayEl = document.querySelector('#switcher-icon-overlay');
                if (overlayEl) overlayEl.checked = true;
                break;
            case 'detached':
                let detachedEl = document.querySelector('#switcher-detached');
                if (detachedEl) detachedEl.checked = true;
                break;
            case 'doublemenu':
                let doubleMenuEl = document.querySelector('#switcher-double-menu');
                if (doubleMenuEl) doubleMenuEl.checked = true;
                break;
            default:
                let defaultMenuEl2 = document.querySelector('#switcher-default-menu');
                if (defaultMenuEl2) defaultMenuEl2.checked = true;
                break;
        }
    }
    // navigation menu style
    if (localStorage.ynexnavstyles) {
        let navStyles = localStorage.getItem('ynexnavstyles');
        switch (navStyles) {
            case 'menu-click':
                let menuClickSwitchEl = document.querySelector('#switcher-menu-click');
                if (menuClickSwitchEl) menuClickSwitchEl.checked = true;
                break;
            case 'menu-hover':
                let menuHoverEl = document.querySelector('#switcher-menu-hover');
                if (menuHoverEl) menuHoverEl.checked = true;
                break;
            case 'icon-click':
                let iconClickSwitchEl = document.querySelector('#switcher-icon-click');
                if (iconClickSwitchEl) iconClickSwitchEl.checked = true;
                break;
            case 'icon-hover':
                let iconHoverSwitchEl = document.querySelector('#switcher-icon-hover');
                if (iconHoverSwitchEl) iconHoverSwitchEl.checked = true;
                break;
        }
    }

    // loader
    if(localStorage.loaderEnable != "true"){
        let loaderDisableEl = document.querySelector("#switcher-loader-disable");
        if (loaderDisableEl) loaderDisableEl.checked = true;
    }
}

